import React, { Component } from "react";
import "./style.css";
import emailjs from 'emailjs-com';

class Contact extends Component {
    constructor(props) {
        super(props);
        this.state = {
          name: '',
          email: '',
          message: ''
        }
      }

  handleSubmit = (e) => {

    e.preventDefault();
    const { name, email, subject, message } = this.state

    let templateParams = {
      From_Name: name,
      From_Email: email,
      to_name: 'service_17adbv7',
      Message: message,
    }

    emailjs.send(
      'Outlook',
      'template_lw77dfo',
      templateParams,
      'user_WREPr2wxcfWjBNM0uTvxy'
    )
    this.resetForm()
  };
  resetForm() {
    this.setState({
      name: '',
      email: '',
      message: '',
    })
  }
  handleChange = (param, e) => {
    this.setState({ [param]: e.target.value })
  }


  render() {
  return (
      <section className="container">
          <div className="p-4">
              <h2 className="contactT text-center">Contact Me</h2>
              <form action="#" className="form-horizontal" onSubmit={this.handleSubmit.bind(this)}>
                  <div className="mb-3">
                      <label for="name" className="form-label">Name:</label>
                      <input
                       type="text"
                       name="name"
                       value={this.state.name}
                       className="text-primary"
                       onChange={this.handleChange.bind(this, 'name')}
                       placeholder="Enter your Name..." />
                  </div>
                  <div className="mb-3">
                      <label for="email" className="form-label">Email address:</label>
                      <input 
                      type="email"
                      name="email"
                      value={this.state.email}
                      className="text-primary"
                      onChange={this.handleChange.bind(this, 'email')}
                      placeholder="Enter email"/>
                  </div>
                  <div className="mb-3">
                      <label for="message" className="form-label">Message:</label>
                      <textarea 
                      type="textarea"
                      name="message"
                      className="text-primary"
                      value={this.state.message}
                      onChange={this.handleChange.bind(this, 'message')}
                      placeholder="Enter a message..."
                          rows="3"></textarea>
                  </div>

                  <div className="mb-3">
                    <button variant="primary" type="submit">Submit</button>
                  </div>
              </form>
                  {this.state.mailSent &&
                    <div>Thank you.</div>
                  }
              <div className="direct-contact-container">
                  <ul className="contact-list">
                      <li className="list-item"><i className="fa fa-map-marker fa-2x"><span className="contact-text place">Marysville, WA</span></i></li>
                      <li className="list-item"><i className="fa fa-envelope fa-2x"><span className="contact-text gmail"><a href="mailto:#" title="Send me an email">danio.2013@outlook.com</a></span></i></li>
                  </ul>
                  <hr></hr>
                  <ul className="social-media-list">
                      <li className="contact-icon"><a href="https://github.com/dorley1993" target="_blank"><i className="icons fab fa-github-square fa-2x"></i></a></li>
                      <li><a className="contact-icon" href="https://www.linkedin.com/in/danielle-orley/" target="_blank"><i className="icons fab fa-linkedin fa-2x"></i></a></li>
                      <li><a className="contact-icon" href="https://www.instagram.com/rainbowdevmonster/ " target="_blank"><i className="icons fab fa-instagram fa-2x"></i></a></li>
                      <li><a className="contact-icon" href="/"><i className="icons fas fa-home fa-2x"></i></a></li>
                  </ul>
                  <hr></hr>
              </div>
          </div>
      </section>
    );
  }
};

export default Contact;
