import React, { Component } from "react";
import "./style.css";
import axios from "axios";

const API_PATH = 'http://localhost:3000/portfolio_v4/api/contact/index.php';

class Contact extends Component {
    constructor(props) {
        super(props);
        this.state = {
          name: '',
          email: '',
          message: '',
          mailSent: false,
          error: null
        }
      }

      handleFormSubmit = e => {
        e.preventDefault();
        axios({
          method: 'post',
          url: `${API_PATH}`,
          headers: { 'content-type': 'application/json' },
          data: this.state
        })
          .then(result => {
            this.setState({
              mailSent: result.data.sent
            })
          })
          .catch(error => this.setState({ error: error.message }));
      };


  render() {
  return (
      <section className="container">
          <div className="p-4">
              <h2 className="contactT text-center">Contact Me</h2>
              <form action="#" className="form-horizontal">
                  <div className="mb-3">
                      <label for="name" className="form-label">Name:</label>
                      <input type="text" 
                      className="form-control" 
                      id="name" name="name"
                      value={this.state.name} 
                      onChange={e => this.setState({ name: e.target.value })}
                      placeholder="Enter First and Last Name..." />
                  </div>
                  <div className="mb-3">
                      <label for="email" className="form-label">Email address:</label>
                      <input type="email" 
                      className="form-control" 
                      id="email" 
                      name="email" 
                      value={this.state.email}
                      onChange={e => this.setState({ email: e.target.value })}
                      placeholder="name@example.com" />
                  </div>
                  <div className="mb-3">
                      <label for="message" className="form-label">Message:</label>
                      <textarea 
                      name="message" 
                      className="form-control" 
                      id="message" 
                      onChange={e => this.setState({ message: e.target.value })}
                      value={this.state.message}
                      placeholder="Enter a message..."
                          rows="3"></textarea>
                  </div>

                  <div className="mb-3">
                     <input 
                     type="submit" 
                     onClick={e => this.handleFormSubmit(e)}
                     value="Submit" />
                  </div>
              </form>
                  {this.state.mailSent &&
                    <div>Thank you.</div>
                  }
              <div className="direct-contact-container">
                  <ul className="contact-list">
                      <li className="list-item"><i className="fa fa-map-marker fa-2x"><span className="contact-text place">Marysville, WA</span></i></li>
                      <li className="list-item"><i className="fa fa-envelope fa-2x"><span className="contact-text gmail"><a href="mailto:#" title="Send me an email">danio.2013@outlook.com</a></span></i></li>
                  </ul>
                  <hr></hr>
                  <ul className="social-media-list">
                      <li className="contact-icon"><a href="https://github.com/dorley1993" target="_blank"><i className="icons fab fa-github-square fa-2x"></i></a></li>
                      <li><a className="contact-icon" href="https://www.linkedin.com/in/danielle-orley/" target="_blank"><i className="icons fab fa-linkedin fa-2x"></i></a></li>
                      <li><a className="contact-icon" href="https://www.instagram.com/rainbowdevmonster/ " target="_blank"><i className="icons fab fa-instagram fa-2x"></i></a></li>
                      <li><a className="contact-icon" href="/"><i className="icons fas fa-home fa-2x"></i></a></li>
                  </ul>
                  <hr></hr>
              </div>
          </div>
      </section>
    );
  }
};

export default Contact;
